var app = require(`${__dirname}/src/app.js`);
var ui = require(`${__dirname}/src/ui.js`);
var tools = require(`${__dirname}/src/tools.js`);

module.exports = { app: app, ui: ui, $e: tools.$, tools: tools,
  appXML: async function (){
    const { app } = require('electron');
    var rootPath = app.getAppPath();
    var x = await tools.loadXMLObject(`${rootPath}/app.xml`);
    return x;
  }
};

