const tools = require(`${__dirname}/tools.js`);

module.exports = {
    loader: function(){
      var e = document.createElement('div');
      e.classList.add('--g-loader');

      $('#app--wrapper')
      return {
        element: e,
        stop: () => {

        }
      }
    },
    xloader: async function(wrapper){
      if(wrapper === undefined) return null;
      wrapper.innerHTML = null; //clean xwrapper
      await tools.importHTML(`./assets/html/loader.html`, wrapper);
    }
};
